<?php

function GetClients($rut){

   $json_url = 'https://api.defontana.com/api/Sale/GetClients?';

   $data = array( 'status'=>0,
                  'legalcode'=>$rut,
                  'itemsPerPage'=>10,
                  'pageNumber'=>1);

   $data = http_build_query($data);

   // Initializing curl
   $ch = curl_init($json_url.$data);

   // Configuring curl options
   
   curl_setopt($ch,CURLOPT_RETURNTRANSFER,true);
   curl_setopt($ch,CURLOPT_HTTPHEADER,array('Content-type: application/json','Authorization: bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..PTHvSm-iG82DiYmQYFjFTuqYHvAicWfhZHCmxV12NwE'));

   // Getting results
   $result = curl_exec($ch); // Getting jSON result string
   $res = array();
   $res = json_decode($result);

   return $res;
}

function clientExists($rut){
   $res = GetClients($rut);
   if($res->totalItems == 0)
      return false;
   else 
      return true;
}
